            <FormField
              control={form.control}
              name="portalName"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Portal</FormLabel>
                  <FormControl>
                    <Input placeholder="e.g. Job Right" {...field} data-testid="input-portal" />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
