            <FormField
              control={form.control}
              name="jobLink"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Job Link</FormLabel>
                  <FormControl>
                    <Input type="url" placeholder="https://..." {...field} data-testid="input-job-link" />
                  </FormControl>
                  <FormMessage />
                </FormItem>
