            <FormField
              control={form.control}
              name="jobPage"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Job Page</FormLabel>
                  <FormControl>
                    <Input type="url" placeholder="https://..." {...field} data-testid="input-job-page" />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="resumeUrl"
              render={({ field }) => (
