              <Button 
                variant="ghost" 
                size="sm" 
                onClick={handleLogout}
                disabled={logout.isPending}
                data-testid="button-logout"
              >
                <LogOut className="w-4 h-4" />
              </Button>
            </div>
          </div>
        </div>
      </div>
    </nav>
  );
}

