export function isUnauthorizedError(error: Error): boolean {
  return /^401: .*/.test(error.message);
}

export function getInitials(name: string): string {
  return name
    .split(" ")
    .map(part => part.charAt(0).toUpperCase())
    .join("")
    .slice(0, 2);
}

export function getStatusColor(status: string): string {
  const statusColors = {
    Applied: "bg-blue-100 text-blue-800",
    Screening: "bg-purple-100 text-purple-800",
    Interview: "bg-yellow-100 text-yellow-800",
    Offer: "bg-orange-100 text-orange-800",
    Hired: "bg-emerald-100 text-emerald-800",
    Rejected: "bg-red-100 text-red-800",
    "On Hold": "bg-gray-100 text-gray-800",
  };

  return statusColors[status as keyof typeof statusColors] || "bg-gray-100 text-gray-800";
}

export function getRoleColor(role: string): string {
  const roleColors = {
    ADMIN: "bg-red-100 text-red-800",
    CLIENT: "bg-green-100 text-green-800",
    EMPLOYEE: "bg-blue-100 text-blue-800",
  };

  return roleColors[role as keyof typeof roleColors] || "bg-gray-100 text-gray-800";
}

client/src/lib/csv-export.ts.content:
import type { ApplicationFilters } from "@/types";

export async function exportApplicationsCSV(filters: ApplicationFilters) {
  const params = new URLSearchParams();

  Object.entries(filters).forEach(([key, value]) => {
    if (value !== undefined && value !== "") {
      params.append(key, value.toString());
    }
  });

  const response = await fetch(`/api/applications/export?${params.toString()}`, {
    credentials: "include",
  });

  if (!response.ok) {
    throw new Error("Failed to export CSV");
  }

  const blob = await response.blob();
  const url = window.URL.createObjectURL(blob);
  const a = document.createElement("a");
  a.href = url;
  a.download = `job_applications_${new Date().toISOString().split("T")[0]}.csv`;
  document.body.appendChild(a);
  a.click();
  window.URL.revokeObjectURL(url);
  document.body.removeChild(a);
}

client/src/lib/queryClient.ts.content:
import { QueryClient, QueryFunction } from "@tanstack/react-query";

async function throwIfResNotOk(res: Response) {
  if (!res.ok) {
    const text = (await res.text()) || res.statusText;
    throw new Error(`${res.status}: ${text}`);
  }
}

export async function apiRequest(
  method: string,
  url: string,
  data?: unknown | undefined,
): Promise<Response> {
  const res = await fetch(url, {
    method,
    headers: data ? { "Content-Type": "application/json" } : {},
    body: data ? JSON.stringify(data) : undefined,
    credentials: "include",
  });

  await throwIfResNotOk(res);
  return res;
}

type UnauthorizedBehavior = "returnNull" | "throw";
export const getQueryFn: <T>(options: {
  on401: UnauthorizedBehavior;
}) => QueryFunction<T> =
  ({ on401: unauthorizedBehavior }) =>
  async ({ queryKey }) => {
    const res = await fetch(queryKey.join("/") as string, {
      credentials: "include",
    });

    if (unauthorizedBehavior === "returnNull" && res.status === 401) {
      return null;
    }

    await throwIfResNotOk(res);
    return await res.json();
  };

export const queryClient = new QueryClient({
  defaultOptions: {
    queries: {
      queryFn: getQueryFn({ on401: "throw" }),
      refetchInterval: false,
      refetchOnWindowFocus: false,
      staleTime: Infinity,
      retry: false,
    },
    mutations: {
      retry: false,
    },
  },
});

